import Imagebody from './imagebody'
import React,{ Component } from 'react';
import {Form,Button,Badge} from 'react-bootstrap'
import { Breadcrumb,Alert, BreadcrumbItem,Spinner } from 'reactstrap';
import './bodyallfile.css';
class Myfile extends Component{
    constructor(props){
        super(props)
        this.state={
            fileloaded:false,
            item:[]
        }
    }
    fetcher=()=>{
        fetch('https://apinotessh.herokuapp.com/allfileinfo')
        .then(res=>res.json())
        .then(json=>{
        
            var arr=json.files
            var arr1=[]
            arr.map(data=>{
                var s=data.filename
                arr1.push(s)
            })
            this.setState({
                fileloaded:true,
                item:arr1
            })
        })
    }
    componentDidMount(){
        this.fetcher();
    }
    render(){
        var {fileloaded,item}=this.state
        if(fileloaded==false){
            return (
                <div>
                    <Alert variant="secondary">
                        My Uploaded Files
                    </Alert>
                </div>
            )
        }
        else{
            return (
                <div>
                    <Alert variant="secondary">
                        My Uploaded Files
