import Imagebody from './imagebody'
import React,{ Component } from 'react';
import {Alert,Form,Button,Badge,FormControl,Navbar,Nav} from 'react-bootstrap'
//import { Breadcrumb,Alert, BreadcrumbItem,Spinner } from 'reactstrap';
import './bodyallfile.css';
class Myfile extends Component{
    constructor(props){
        super(props)
        this.state={
            fileloaded:false,
            item:[]
        }
    }
    fetcher=()=>{
        fetch('https://apinotessh.herokuapp.com/allfileinfo')
        .then(res=>res.json())
        .then(json=>{
        
            var arr=json.files
            var arr1=[]
            arr.map(data=>{
                var s=data.filename
                arr1.push(s)
            })
            this.setState({
                fileloaded:true,
                item:[]
            })
        })
    }
    componentDidMount(){
        this.fetcher();
    }
    render(){
        var {fileloaded,item}=this.state
        if(fileloaded==false){
            return (
                <div>
                    <Navbar bg="light" variant="light">
                        <Navbar.Brand>All Files</Navbar.Brand>
                        <Nav className="mr-auto">
                        </Nav>
                        <Form inline>
                        <FormControl type="text" placeholder="Search for Files" className="mr-sm-2" onChange={this.changer}/>
                        <Button variant="outline-primary">Search</Button>
                        </Form>
                    </Navbar>
                </div>
            )
        }
        else if(item.length==0){
            return (
                <div>
                    <Navbar bg="light" variant="light">
                        <Navbar.Brand>All Files</Navbar.Brand>
                        <Nav className="mr-auto">
                        </Nav>
                        <Form inline>
                        <FormControl type="text" placeholder="Search for Files" className="mr-sm-2" onChange={this.changer}/>
                        <Button variant="outline-primary">Search</Button>
                        </Form>
                    </Navbar>
                    <br/>
                    <Alert variant='danger'>
                        No Files to show
                    </Alert>
                </div>
            )
        }
        else{
            return (
                <div>
                    <Navbar bg="light" variant="light">
                        <Navbar.Brand>My Uploads</Navbar.Brand>
                        <Nav className="mr-auto">
                        </Nav>
                        {/* <Form inline>
                        <FormControl type="text" placeholder="Search for Files" className="mr-sm-2" onChange={this.changer}/>
                        <Button variant="outline-primary">Search</Button>
                        </Form> */}
                    </Navbar>
                    <br/>
                    <div className="adjustfile">
                        {item.map(seconddata=>
                            <Imagebody filer={seconddata} />
                        )}
                    </div><br/>
                </div>
            )
        }
    }
}


export default Myfile
