import React,{ Component } from 'react';
import Answercard from './answercard'
import {Form,FormGroup,CustomInput,InputGroup,InputGroupAddon,Input,Spinner} from 'reactstrap'
import './bodyallfile.css';

class Answerof extends Component{
    constructor(props){
        super(props)
        this.state={
            urlvalue:[],
            items:[],
            load:false,
          }
    }
    fetcher=()=>{
        fetch('https://apinotessh.herokuapp.com/comment')
        .then(res=>res.json())
        .then(json=>{
          var arr=json.data
          this.setState({
            urlvalue:this.state.urlvalue,
            load:true,
            items:arr
          })
        })
      }
      componentDidMount(){
        this.fetcher();
        this.interval=setInterval(()=>{this.fetcher()},1000)
      }
      componentWillUnmount() {
        clearInterval(this.interval);
      }
    inputofanswer=(event)=>{
      let val=event.target.value
      var questionarray=this.state.items
      questionarray.map((value)=>{
        let question=value.question
        let check=question.includes(val)
        if(check==true){
          console.log(value)
        }
      })

    }
    render(){
        var {load,items}=this.state
        //console.log(items)
        if(load==true){
          return (
              <div>
                <div>
                    <Form className="questionfindform">
                    <InputGroup size="lg" className="Adjustinput">
                      <Input placeholder="Enter the keyword" onChange={this.inputofanswer}/>
                    </InputGroup>
                    </Form>
                  </div>
                  {items.map(data=>
                      <Answercard orgvalue={data} id={data.question}/>
                  )}
              </div>
          )
        }
        else{
          return (
              <div>
                  <center>
                    <Spinner style={{ width: '3rem', height: '3rem' }} />{' '}
                    <Spinner style={{ width: '3rem', height: '3rem' }} type="grow" />
                  </center>
              </div>
          )
        }    
    }
}


export default Answerof
